mapper.update:
UPDATE BOARD
SET BOARD_VIEW = BOARD_VIEW+1
where BOARD_NO = #{boardNo}

UPDATE board
SET BOARD_TITLE = #{boardTitle}, BOARD_SUMMARY = #{boardSummary}
WHERE BOARD_NO = #{boardNo}

mapper.select:
SELECT BOARD_NO AS boardNo, USER_NICKNAME AS userNickname, BOARD_TITLE AS boardTitle, BOARD_SUMMARY AS boardSummary, BOARD_VIEW AS boardView, DATE_FORMAT(INPUT_DATE, '%y-%m-%d') AS inputDate , RECOMMEND AS recommend, LOCAL_CATEGORY AS localCategory FROM board
BOARD_TITLE LIKE CONCAT('%',#{keyword},'%') OR BOARD_SUMMARY LIKE CONCAT('%',#{keyword},'%')
USER_NICKNAME LIKE CONCAT('%',#{keyword},'%')
LOCAL_CATEGORY = #{localCategory}
LOCAL_CATEGORY = #{localCategory}
ORDER BY BOARD_NO DESC

mapper.update:
UPDATE BOARD
SET recommend = recommend+1
WHERE BOARD_NO =#{boardNo}

UPDATE BOARD
SET recommend = recommend-1
WHERE BOARD_NO =#{boardNo}

mapper.select:
SELECT COUNT(*)
FROM RECOMMEND
WHERE user_id = #{userId} and BOARD_NO = #{boardNo}
